REPLACE INTO `lat_local_skin` (`label`, `pg`, `sid`, `skin`) VALUES('smilies_pop', 'global', 1, '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="{$this->lat->lang[''xmllang'']}" lang="{$this->lat->lang[''xmllang'']}">\r\n<head>\r\n<title>{$this->lat->lang[''smilies'']}</title>\r\n<!-- HEAD -->\r\n</head>\r\n<body class="body_no_padding">\r\n<!-- JS -->\r\n<script type="text/javascript"><!--\r\nfunction smilieInsert(smilie)\r\n{\r\n	smilie = " " + smilie + " ";\r\n\r\n	if (opener.document.post.data.createTextRange)\r\n	{\r\n		opener.document.post.data.focus();\r\n		var sel = opener.document.selection;\r\n		var range = sel.createRange();\r\n		range.colapse;\r\n\r\n		range.text = smilie;\r\n		range.collapse(true);\r\n		range.moveEnd(''character'', -smilie.length);\r\n		range.moveStart(''character'', smilie.length);\r\n		range.select();\r\n	}\r\n	else if (opener.document.post.data.selectionStart >= 0)\r\n	{\r\n		var sel = opener.document.post.data.value.substr(opener.document.post.data.selectionStart, opener.document.post.data.selectionEnd - opener.document.post.data.selectionStart);\r\n		var selstart = opener.document.post.data.selectionStart;\r\n		var scrollp = opener.document.post.data.scrollTop;\r\n\r\n		opener.document.post.data.value = opener.document.post.data.value.substr(0, selstart) + sel + smilie + opener.document.post.data.value.substr(opener.document.post.data.selectionEnd);\r\n\r\n		if (opener.document.post.data.setSelectionRange)\r\n		{\r\n			opener.document.post.data.setSelectionRange(selstart + smilie.length, selstart + smilie.length);\r\n\r\n			opener.document.post.data.focus();\r\n		}\r\n\r\n		opener.document.post.data.scrollTop = scrollp;\r\n	}\r\n	else\r\n	{\r\n		opener.document.post.data.value += smilie;\r\n		opener.document.post.data.focus(opener.document.post.data.value.length - 1);\r\n	}\r\n}\r\n--></script>\r\n<h1>{$this->lat->lang[''smilies'']}</h1>\r\n<table cellpadding="0" cellspacing="0" class="table_bdr" width="100%">\r\n	<tr>\r\n		<th width="50%">\r\n			{$this->lat->lang[''text'']}\r\n		</th>\r\n		<th width="50%">\r\n			{$this->lat->lang[''smiley'']}\r\n		</th>\r\n	</tr>{$smilies}\r\n</table>\r\n</body>\r\n</html>');
